public class User {
	private String name,
				   surname;
	private Calendar birthday;
	private String username,
				   password;

	public boolean passwordMatch(String password) {
		return this.password.equals(password);
	}
	
	public void setPassword(String password){
		this.password = password;
	}

	public boolean usernameMatch(String username) {
		return this.getUsername().equals(username);
	}
	
	public User(String name,String surname,Calendar birthday,String username, String password){
		this.name = name;
		this.surname = surname;
		this.birthday = birthday;
		this.setUsername(username);
		this.password = password;
	}
	
	public static String new_random_password(){
		Random random_number = new Random();
		Long password_number = random_number.nextLong();
		return password_number.toString();
	}
	
	public String toString(){
		return String.format("Name: %s\n Surname: %s\n Birthday: %s\n User:%s Password:%s ",
					name,
					surname,
					birthday.toString(),
					getUsername(),
					password);
	}

	public String getUsername() {
		return username;
	}

	public void setUsername(String username) {
		this.username = username;
	}
}
